import styled from "styled-components";

export const HeaderTag = styled.header`
  background-color: var(--brand-color);
  border: 1px solid red;
  @media (min-width: 767px) {
    display:flex;
    align-items:center;
  }
`;
export const Search = styled.div`
  border: 1px solid red;
  & input[type="text"]{
    color: gray
  }
  & input[type="submit"]{
    color: gray
  }

`;
